window.Gantt = class {
  constructor(selector, tasks) {
    const container = document.querySelector(selector);
    container.innerHTML = "";
    tasks.forEach(task => {
      const bar = document.createElement("div");
      bar.textContent = `${task.name} (${task.start} â†’ ${task.end})`;
      bar.className = "bg-purple-200 my-1 px-2 py-1 rounded";
      container.appendChild(bar);
    });
  }
};
