
function Gantt(selector, tasks, options = {}) {
  console.log("Gantt inicializado com:", selector, tasks, options);
  const container = document.querySelector(selector);
  if (container) {
    container.innerHTML = "<div style='color: green; text-align: center;'>[Gantt renderizado]</div>";
  }
}
window.Gantt = Gantt;
