
// Versão simplificada real funcional para teste com barra
function Gantt(selector, tasks, options = {}) {
  const container = document.querySelector(selector);
  container.innerHTML = "";
  tasks.forEach((t, i) => {
    const bar = document.createElement("div");
    bar.style.background = "#4ade80";
    bar.style.margin = "4px 0";
    bar.style.height = "24px";
    bar.style.width = Math.max(10, (new Date(t.end) - new Date(t.start)) / (1000 * 60 * 60 * 24) * 20) + "px";
    bar.title = `${t.name}: ${t.start} → ${t.end}`;
    bar.textContent = t.name;
    bar.style.color = "white";
    bar.style.paddingLeft = "8px";
    bar.style.fontSize = "12px";
    bar.style.display = "flex";
    bar.style.alignItems = "center";
    bar.style.borderRadius = "4px";
    container.appendChild(bar);
  });
  console.log("Gantt real simulado inicializado com:", selector, tasks, options);
}
window.Gantt = Gantt;
